import React from 'react';
import Card from 'react-bootstrap/Card';
import logo from '../images/shoes.png';

const ItemCarrito = ({data, deleteFromCart}) => {
  const {id,modelo,precio,cantidad } = data
  return (
    <div>
        <h4>{modelo}</h4>
        <h5>
          $ {precio} x {cantidad} = $ {precio * cantidad}
        </h5>
        <button onClick={() => deleteFromCart(id, false)} className='btn btn-outline-primary mr-1'>
          Eliminar uno
        </button>
        <button className='btn btn-outline-primary mr-1' onClick={() => deleteFromCart(id,true)}> Eliminar todos</button>
    </div> 
  )
}

export default ItemCarrito
